import React, { useState } from "react";

export default function TemperatureUnitButton() {
    const [unit, setUnit] = useState("celsius");

    if (unit == "celsius"){
      return (
        <button type="button" className="temperature-unit-button" onClick>
          °F
        </button>
      );
    } else {
        return (
        <button type="button" className="temperature-unit-button">
          °C
        </button>
        )}
}
