import React, { useState } from "react";
import axios from "axios";
import "./Weather.css";

export default function SearchEngine() {
  const [city, setCity] = useState(null);
  const [updatedCity, setUpdatedCity] = useState(null);
  const [temperature, setTemperature] = useState(null);
  const [weatherDescription, setWeatherDescription] = useState(null);
  const [humidity, setHumidity] = useState(null);
  const [wind, setWind] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);

  function sendApiRequest(city) {
    let apiKey = "a96f5721c6287ed7127e00501efd3d4f";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(getApiInfo);
  }

  function handleSubmit(event) {
    event.preventDefault();
    setCity(updatedCity);
  }

  function updateCity(event) {
    setUpdatedCity(event.target.value);
  }

  function getApiInfo(response) {
    setTemperature(Math.round(response.data.main.temp));
    setWeatherDescription(response.data.weather[0].description);
    setHumidity(response.data.main.humidity);
    setWind(response.data.wind.speed);
    setImageUrl(
      `http://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`
    );
  }

  function weatherDescriptionDisplay() {
    if (city === null) {
      return "d-none";
    } else {
      sendApiRequest(city);
      return "d-block";
    }
  }

  return (
    <div className="SearchEngine">
      <form onSubmit={handleSubmit}>
        <input
          type="search"
          placeholder="Enter a city..."
          onChange={updateCity}
        />
        <input type="submit" value="Search" />
      </form>
      <div className={weatherDescriptionDisplay()}>
        <h3>Current weather in {city}</h3>
        <ul>
          <li>Temperature: {temperature}Â°C </li>
          <li>Description: {weatherDescription} </li>
          <li>Humidity: {humidity}% </li>
          <li>Wind: {wind}km/h</li>
          <li>
            <img src={imageUrl} alt="Weather Icon" />
          </li>
        </ul>
      </div>
    </div>
  );
}
